        </Text>
      </View>
      <View style={styles.textInputContainer}>
        {newPasswordRequired ? (
          <RenewPwdScreen
            pwd={newPassword}
            setNewPasswordRequired={pwd => setNewPassword(pwd)}
            confirmNewPwd={() => confirmNewPwd()}
          />
        ) : (
          <View>
            {hasAuthApp ? (
