import document from '@backend/api/ownspaceapi/resolvers/document';
import user from '@backend/api/ownspaceapi/resolvers/user';
import { Auth, Storage } from 'aws-amplify';

/**
 * Api object that return all the promises
 */
const api = {
  // DOCUMENT
  /**
   * Create a file in txt format
   * @param {object} payload - File payload
   */
  createFileTxt(payload) {
    return Promise.resolve(
      document.Mutation.createFileTxt({
        name: payload.name,
        content: payload.content,
        owner: payload.owner,
        parent: payload.parent,
        createdAt: payload.createdAt,
        updatedAt: payload.updatedAt
      })
    );
  },
  /**
   * Create a folder
   * @param {object} payload - Folder payload
   */
  createFolder(payload) {
    return Promise.resolve(
      document.Mutation.createFolder({
        name: payload.name,
        owner: payload.owner,
        parent: payload.parent,
        createdAt: payload.createdAt,
        updatedAt: payload.updatedAt
      })
    );
  },
  /**
   * Load the folders
   * @param {object} payload - Folder parent of folders to load and the current user
   */
  loadFolders(payload) {
    return Promise.resolve(
      document.Query.getAllFolders({
        parent: payload.pathId,
        user: payload.owner
      })
    );
  },
  /**
   * Load the files
   * @param {object} payload - Folder parent of files to load and the current user
   */
  loadFiles(payload) {
    return Promise.resolve(
      document.Query.getAllFiles({
        parent: payload.pathId,
        user: payload.userId
      })
    );
  },
  /**
   * Download the file selected
   * @param {object} payload - Path of the file to download
   */
  async downloadFile(payload) {
    const res = await Storage.get(payload.path, { level: 'private' })
      .then(result => {
        return result;
      })
      .catch(err => console.log(err));
    return res;
  },
  /**
   * Add selected file to DB
   * @param {object} payload - File payload
   */
  addSelectedFile(payload) {
    return Promise.resolve(
      document.Mutation.createFile({
        name: payload.name,
        owner: payload.owner,
        parent: payload.parent,
        createdAt: payload.createdAt,
        updatedAt: payload.updatedAt,
        size: payload.size,
        type: payload.type
      })
    );
  },
  /**
   * Add selected file to S3
   * @param {object} payload - File payload
   */
  async addSelectedFileToS3(payload) {
    try {
      const response = await fetch(payload.absolutePath);
      const blob = await response.blob();

      return Promise.resolve(
        Storage.put(payload.path, blob, {
          level: 'private',
          contentType: payload.type,
          progressCallback(progress) {
            if (progress.loaded === progress.total) {
              return 200;
            }
          }
        })
      );
    } catch (err) {
      console.log(err);
    }
  },
  // USER
  /**
   * Create the user object when the user log for the first time
   * @param {object} payload - Id, email, password and role of the user
   */
  createUser(payload) {
    return Promise.resolve(
      user.Mutation.createUser({
        id: payload.id,
        email: payload.email,
        password: payload.password,
        role: payload.role
      })
    );
  },
  /**
   * Get the user
   * @param {object} payload - Id of the user
   */
  loadUser(payload) {
    return Promise.resolve(
      user.Query.getUser({
        id: payload.userId
      })
    );
  },
  /**
   * Update the user firstname and lastname
   * @param {object} payload - Id, firstname and lastname of the user
   */
  updateUserNames(payload) {
    return Promise.resolve(
      user.Mutation.updateUserNames({
        id: payload.id,
        firstname: payload.firstname,
        lastname: payload.lastname
      })
    );
  },
  /**
   * Update the user password
   * @param {object} payload - Id and password of the user
   */
  updateUserPwdDB(payload) {
    return Promise.resolve(
      user.Mutation.updateUserPwdDB({
        id: payload.id,
        password: payload.newPassword
      })
    );
  },

  /**
   * Change the user password in cognito
   * @param {object} payload - Id, old password and new password of the user
   */
  changeUserPassword(payload) {
    return Promise.resolve(
      user.Query.changeUserPassword({
        id: payload.id,
        oldPassword: payload.oldPassword,
        newPassword: payload.newPassword
      })
    );
  },
  /**
   * Sign out the user from the application
   */
  async signOutUser() {
    try {
      await Auth.signOut();
    } catch (error) {
      console.log('error signing out: ', error);
    }
  }

  // GROUP
  // RIGHTS
};

export default api;

import React from 'react';
import { View, TouchableWithoutFeedback, StyleSheet } from 'react-native';
import Text from '@shared/ClientText';
import Icon from 'react-native-vector-icons/Feather';

/**
 * The BackFolder component
 * @param {function} backAction - The function to start when user go back
 * @param {boolean} visible - Tell if the BackFolder component is visible or not
 * @param {string} name - The name of the previous folder
 */
const BackFolder = ({ backAction, visible, name }) => {
  if (visible) {
    /**
     * Render the BackFolder component
     * @returns {React.Component} - BackFolder component
     */
    return (
      <TouchableWithoutFeedback onPress={() => backAction()}>
        <View style={styles.backContainer}>
          <Icon size={18} color="#003466" name="arrow-left" />
          <View style={styles.backTitle}>
            <Text style={styles.backText}>{name}</Text>
          </View>
        </View>
      </TouchableWithoutFeedback>
    );
  }
  return null;
};

/**
 * Styles of BackFolder component
 */
const styles = StyleSheet.create({
  backContainer: {
    flexDirection: 'row',
    margin: 15,
    alignItems: 'center'
  },
  backTitle: {
    marginLeft: 7
  },
  backText: {
    fontWeight: '900',
    color: '#555459',
    fontSize: 16
  }
});

export default BackFolder;

import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Platform } from 'react-native';
import Text from '@shared/ClientText';
import FileIcon from 'react-native-vector-icons/MaterialCommunityIcons';
import Icon from 'react-native-vector-icons/Feather';
import IconOptions from 'react-native-vector-icons/Entypo';
import formatDate from '@utils/formatDate';
import {
  Menu,
  MenuOptions,
  MenuOption,
  MenuTrigger,
  renderers
} from 'react-native-popup-menu';
import i18n from '@i18n/i18n';
import { ifIphoneX } from 'react-native-iphone-x-helper';
import { AnimatedCircularProgress } from 'react-native-circular-progress';
import { useDispatch, connect } from 'react-redux';
import {
  openFile,
  requestAndroidWriteExternalStorage
} from '@utils/fileManager';
import RNFetchBlob from 'rn-fetch-blob';
import { getCurrentPathString } from '@store/modules/document/selectors';
import { createSelector } from 'reselect';
import { downloadFile, addFileToCache } from '@store/modules/document/actions';

/**
 *  A hash map containing commonly used folders
 */
const { dirs } = RNFetchBlob.fs;

/**
 * Connect to the store and extract data
 */
const mapStateToProps = createSelector(
  getCurrentPathString,
  currentPathString => {
    return {
      currentPathString
    };
  }
);

/**
 * Animation that makes slide in the menu from the bottom of the screen.
 */
const { SlideInMenu } = renderers;

/**
 * The File component
 * @param {object} file - The file
 * @param {string} currentPathString - The current path
 */
const File = ({ file, currentPathString }) => {
  const [progress, setProgress] = useState(0);
  const dispatch = useDispatch();
  const [isDownloading, setIsDownloading] = useState(false);

  /**
   * Open the document
   */
  const _openFile = () => {
    if (!isDownloading) {
      setIsDownloading(true);
      return new Promise((resolve, reject) => {
        const payload = {
          path: currentPathString + file.name,
          resolve,
          reject
        };
        dispatch(downloadFile(payload));
      })
        .then(async value => {
          const SavePath =
            Platform.OS === 'ios' ? dirs.CacheDir : dirs.DownloadDir;

          if (Platform.OS === 'android') {
            if (!requestAndroidWriteExternalStorage()) {
              return;
            }
          }

          RNFetchBlob.config({
            path: `${SavePath}/${file.name}`
          })
            .fetch('GET', value, {})
            .progress({}, (received, total) => {
              setProgress((received / total) * 100);
            })
            .then(res => {
              dispatch(addFileToCache(res.path()));
              setProgress(100);
              setTimeout(() => {
                setProgress(0);
              }, 800);
              openFile(res.path());
              setIsDownloading(false);
            });
        })
        .catch(error => {
          setIsDownloading(false);
          console.log('error', error);
        });
    }
  };

  /**
   * Set the right icon to the file
   * @param {string} type - The file type
   */
  const _setIcon = type => {
    switch (type) {
      case 'text/plain':
        return 'file-document';
      case 'image/jpeg':
        return 'file-image';
      case 'application/pdf':
        return 'file-pdf';
      case 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet':
        return 'file-table';
      case 'application/vnd.openxmlformats-officedocument.wordprocessingml.document':
        return 'file-word';
      case 'application/vnd.openxmlformats-officedocument.presentationml.presentation':
        return 'file-powerpoint';
      default:
        return 'file';
    }
  };

  /**
   * Render the File component
   * @returns {React.Component} - File component
   */
  return (
    <View style={styles.container}>
      <View style={styles.rowFile}>
        <TouchableOpacity onPress={() => _openFile()}>
          <View style={styles.containerFile}>
            <FileIcon size={36} color="#003466" name={_setIcon(file.type)} />
            <View style={styles.fileItem}>
              <Text
                style={styles.fileName}
                numberOfLines={1}
                ellipsizeMode="tail"
              >
                {file.name}
              </Text>
              <View style={styles.detailsContainer}>
                <Text style={styles.fileDate}>{formatDate(file)}</Text>
              </View>
            </View>
            <View style={styles.circularProgress}>
              <AnimatedCircularProgress
                size={22}
                width={2}
                fill={progress}
                tintColor="#003466"
                backgroundColor="white"
              />
            </View>
          </View>
        </TouchableOpacity>
        <View style={styles.containerMenu}>
          <Menu renderer={SlideInMenu}>
            <MenuTrigger>
              <IconOptions
                name="dots-three-horizontal"
                size={20}
                color="#858585"
              />
            </MenuTrigger>
            <MenuOptions
              customStyles={{
                optionsContainer: {
                  borderTopLeftRadius: 16,
                  borderTopRightRadius: 16
                },
                optionsWrapper: {
                  ...ifIphoneX(
                    {
                      paddingBottom: 30
                    },
                    {
                      paddingBottom: 10
                    }
                  ),
                  paddingTop: 10
                }
              }}
            >
              <View>
                <MenuOption
                  customStyles={{
                    optionWrapper: styles.menuOptions
                  }}
                  value={1}
                  onSelect={() => console.log('file', file)}
                >
                  <View style={styles.options}>
                    <Icon name="user-plus" size={25} color="#003466" />
                    <Text style={styles.textMenuOptions}>
                      {i18n.t('document.share')}
                    </Text>
                  </View>
                </MenuOption>
                <MenuOption
                  customStyles={{
                    optionWrapper: styles.menuOptions
                  }}
                  value={2}
                  onSelect={() => console.log('file', file)}
                >
                  <View style={styles.options}>
                    <Icon name="lock" size={25} color="#003466" />
                    <Text style={styles.textMenuOptions}>
                      {i18n.t('document.protect')}
                    </Text>
                  </View>
                </MenuOption>
                <MenuOption
                  customStyles={{
                    optionWrapper: styles.menuOptions
                  }}
                  value={3}
                  onSelect={() => console.log('file', file)}
                >
                  <View style={styles.options}>
                    <Icon name="edit" size={25} color="#003466" />
                    <Text style={styles.textMenuOptions}>
                      {i18n.t('document.renameFile')}
                    </Text>
                  </View>
                </MenuOption>
                <MenuOption
                  customStyles={{
                    optionWrapper: styles.menuOptionsLast
                  }}
                  value={4}
                  onSelect={() => console.log('file', file)}
                >
                  <View style={styles.options}>
                    <Icon name="trash" size={25} color="#003466" />
                    <Text style={styles.textMenuOptions}>
                      {i18n.t('document.deleteFile')}
                    </Text>
                  </View>
                </MenuOption>
              </View>
            </MenuOptions>
          </Menu>
        </View>
      </View>
      <View style={styles.separator} />
    </View>
  );
};

/**
 * Styles of File component
 */
const styles = StyleSheet.create({
  container: { flex: 1 },
  rowFile: {
    flex: 1,
    flexDirection: 'row'
  },
  containerFile: {
    flex: 1,
    flexDirection: 'row',
    marginLeft: 20,
    paddingBottom: 12
  },
  separator: {
    marginLeft: 75,
    borderBottomColor: '#A2A8AE',
    borderBottomWidth: 0.4,
    marginBottom: 12
  },
  containerMenu: {
    flex: 1,
    alignItems: 'flex-end',
    justifyContent: 'center',
    paddingBottom: 12,
    paddingRight: 12
  },
  fileItem: {
    flexDirection: 'column',
    justifyContent: 'center',
    marginLeft: 8,
    width: 240
  },
  fileName: {
    flexWrap: 'wrap',
    paddingHorizontal: 12
  },
  detailsContainer: {
    flexDirection: 'row'
  },
  fileDate: {
    paddingLeft: 13,
    fontSize: 10
  },
  menuOptions: {
    margin: 7,
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    marginLeft: 15,
    borderBottomWidth: 0.3,
    borderColor: '#ccc',
    paddingBottom: 15
  },
  menuOptionsLast: {
    margin: 7,
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    marginLeft: 15
  },
  textMenuOptions: {
    marginLeft: 20
  },
  options: {
    flexDirection: 'row',
    alignItems: 'center'
  },
  circularProgress: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center'
  }
});

export default connect(mapStateToProps)(File);

