import { updateUserPassword } from '@store/modules/user/actions';
import showToast from '@utils/showToast';

/**
 * The PasswordModal component
 * @param {object} user - The user object
 */
const PasswordModal = ({ user }) => {
  useFonts({
    // eslint-disable-next-line global-require
    DejaVuSans: require('../../../assets/fonts/DejaVuSans.ttf')
  });
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const dispatch = useDispatch();

  /**
   * Update the password
   */
  const updatePassword = () => {
    if (oldPassword !== '' && newPassword !== '' && confirmPassword !== '') {
      if (newPassword === confirmPassword) {
        if (newPassword.length >= 8) {
          const payload = {
            id: user.id,
            oldPassword,
            newPassword
          };
          dispatch(updateUserPassword(payload));
          setNewPassword('');
          setOldPassword('');
          setConfirmPassword('');
        } else {
          showToast(i18n.t('userProfile.error3'), true);
        }
      } else {
        showToast(i18n.t('userProfile.error2'), true);
      }
    } else {
      showToast(i18n.t('userProfile.error1'), true);
    }
  };

  /**
   * Render the PasswordModal component
   * @returns {React.Component} - PasswordModal component
   */
  return (
    <View style={styles.globalContainer}>
      <Header />
      <View style={styles.container}>
        <View style={styles.containerInfo}>
          <Text style={styles.titleInfo}>
            {i18n.t('userProfile.passwordInfo')}
          </Text>
        </View>
        <Text style={styles.titleInput}>
          {i18n.t('userProfile.oldPassword')}
        </Text>
        <TextInput
          style={styles.input}
          placeholder=""
          placeholderTextColor="grey"
          selectionColor="grey"
          textContentType="password"
          secureTextEntry={true}
          onChangeText={txt => {
            setOldPassword(txt);
          }}
          autoCapitalize="none"
          value={oldPassword}
          returnKeyType="next"
        />
        <Text style={styles.titleInput}>
          {i18n.t('userProfile.newPassword')}
        </Text>
        <TextInput
          style={styles.input}
          placeholder=""
          placeholderTextColor="grey"
          selectionColor="grey"
          textContentType="password"
          secureTextEntry={true}
          onChangeText={txt => {
            setNewPassword(txt);
          }}
          autoCapitalize="none"
          value={newPassword}
          returnKeyType="next"
        />
        <Text style={styles.titleInput}>
          {i18n.t('userProfile.confirmNewPassword')}
        </Text>
        <TextInput
          style={styles.input}
          placeholder=""
          placeholderTextColor="grey"
          selectionColor="grey"
          textContentType="password"
          secureTextEntry={true}
          onChangeText={txt => {
            setConfirmPassword(txt);
          }}
          autoCapitalize="none"
          value={confirmPassword}
          returnKeyType="next"
        />
        <View style={styles.btnContainer}>
          <Button
            mode="contained"
            uppercase={false}
            labelStyle={styles.btnText}
            contentStyle={styles.btnDimension}
            style={styles.btnCancel}
            onPress={() => Actions.pop()}
          >
            {i18n.t('userProfile.cancel')}
          </Button>
          <Button
            mode="contained"
            uppercase={false}
            labelStyle={styles.btnText}
            contentStyle={styles.btnDimension}
            style={styles.btnUpdate}
            onPress={() => updatePassword()}
          >
            {i18n.t('userProfile.update')}
          </Button>
        </View>
      </View>
    </View>
  );
};

