/**
 * Hash the password in Base64
 * @param {string} plaintext - The plaintext password
 */
export const hashPassword = async plaintext => {
  const res = await Base64.encode(plaintext);
  return res;
};

/**
 * Decrypt the password in Base64
 * @param {string} plaintext - The plaintext password
 */
export const decryptPassword = async plaintext => {
  const res = await Base64.decode(plaintext);
  return res;
};

import Toast from 'react-native-root-toast';

/**
 * Show a toast message
 * @param {string} content - The message to display
 * @param {boolean} long - True for long else short
 */
const showToast = (content, long) => {
  if (long) {
    Toast.show(content, {
      duration: Toast.durations.LONG,
      position: Toast.positions.TOP + 30,
      shadow: false,
      opacity: 1
    });
  } else {
    Toast.show(content, {
      duration: Toast.durations.SHORT,
      position: Toast.positions.TOP + 30,
      shadow: false,
      opacity: 1
    });
  }
};

export default showToast;
